oxy_webhelp_navlinks = {"locacc_toc-d4802e4276":{"topics":[{"title":"Getting started with Locations and Accounts","shortdesc":"<p class=\"shortdesc\">When connecting to servers, NotesÂ® uses Location documents and Account documents to configure those connections. A Location document contains information that Notes needs, in order to manage your connections whenever you change the way you're connecting to servers; for example, your Home location may use remote access software in your operating system to dial your Internet server, while your Online location assumes that you're connected to a LAN. An Account document contains information, such as user name and password, for an Internet connection such as Internet mail.<\/p>","href":"locacc_locs_accs_c.html","attributes":{"data-id":"locacc_locs_accs_c"},"menu":{"hasChildren":true},"tocID":"locacc_locs_accs_c-d4802e4287","next":"locacc_locs_accs_c-d4802e4287"},{"title":"Locations and Accounts quick reference","shortdesc":"<p class=\"shortdesc\">This quick reference card is a list of instructions for common tasks and shortcuts for Notes locations and accounts , which you can print and keep at your desk for easy reference.<\/p>","href":"locacc_quickref_r.html","attributes":{"data-id":"PreferencesQuickReference"},"menu":{"hasChildren":false},"tocID":"PreferencesQuickReference-d4802e4501","topics":[]},{"title":"Working with Locations, Accounts, and Connections","shortdesc":"<p class=\"shortdesc\">You set preferences for your Location, Account, and Connection documents that provide the information needed by components to connect to servers. Preferences enable you to create, edit, or delete certain Location, Account, or Connection documents.<\/p>","href":"locacc_prefs.html","attributes":{"data-id":"pref_basic.dita"},"menu":{"hasChildren":true},"tocID":"pref_basic.dita-d4802e4512","next":"pref_basic.dita-d4802e4512"},{"title":"Advanced Locations and Accounts topics","shortdesc":"<p class=\"shortdesc\">Learn to do additional tasks with Location, Account, and Connection documents, such as customizing Location settings, enabling Java applets in Notes, and using iNotes to access your Notes mail from the internet.<\/p>","href":"locacc_adv.html","attributes":{"data-id":"pref_adv.dita"},"menu":{"hasChildren":true},"tocID":"pref_adv.dita-d4802e4871","next":"pref_adv.dita-d4802e4871"}]}};