oxy_webhelp_navlinks = {"rep_roam_adv-d4802e5973":{"topics":[{"title":"Can I toggle between roaming and non-roaming status?","shortdesc":"<p class=\"shortdesc\">A Notes power user can disable and re-enable roaming user status using a combination of NOTES.INI settings. This enables users working across low band width or otherwise slow Internet connection to temporarily disable their roaming status and more quickly access a target Notes application, view, or document, including their Mail inbox. Roaming user status can also be set to either remain disabled or to be re-enabled at subsequent Notes start-up.<\/p>","href":"rep_roaming_onthefly_t.html","attributes":{"data-id":"rep_roaming_onthefly_t"},"menu":{"hasChildren":false},"tocID":"rep_roaming_onthefly_t-d4802e5991","topics":[]}]}};