oxy_webhelp_navlinks = {"wid_app_overview_c-d4802e8118":{"topics":[{"title":"What Widgets and Live Text defaults are provided?","shortdesc":"<p class=\"shortdesc\">Enabling the <span class=\"ph uicontrol\">Show Widget Toolbar and the My Widgets Sidebar panel<\/span> option displays the My Widgets sidebar panel and the Widgets menus and toolbar. However, the administrator can use policy or preferences to control user access to various aspects of Widgets and Live Text functionality.<\/p>","href":"wid_default_outofbox_c.html","attributes":{"data-id":"N10001"},"menu":{"hasChildren":false},"tocID":"N10001-d4802e8144","topics":[]},{"title":"What Widgets preferences can I set?","shortdesc":"<p class=\"shortdesc\">You can use the Widgets preferences panel to specify the name of the widget catalog server, catalog, and catalog categories to install. Also use the Widgets preferences panel to toggle display of the Widgets toolbar buttons, the <span class=\"ph menucascade\"><span class=\"ph uicontrol\">Tools<\/span><abbr title=\"and then\"> &gt; <\/abbr><span class=\"ph uicontrol\">Widgets<\/span><\/span> menu options, and the My Widgets sidebar panel.<\/p>","href":"wid_prefs_widgets_t.html","attributes":{"data-id":"wid_prefs_widgets_t"},"menu":{"hasChildren":false},"tocID":"wid_prefs_widgets_t-d4802e8180","topics":[]},{"title":"What Live Text preferences can I set?","shortdesc":"<p class=\"shortdesc\">You can control the display of Live Text using preference panel options. <\/p>","href":"wid_prefs_live_text_t.html","attributes":{"data-id":"wid_prefs_live_text_t"},"menu":{"hasChildren":false},"tocID":"wid_prefs_live_text_t-d4802e8213","topics":[]}]}};