oxy_webhelp_navlinks = {"con_settings_c.dita-d4800e3328":{"topics":[{"title":"How do I show, hide, or change the location of the preview pane in Contacts?","shortdesc":"<p class=\"shortdesc\">You can show or hide the preview pane, and you can display it on the bottom or the side.<\/p>","href":"con_preview_t.html","attributes":{"data-id":"con_preview_t"},"menu":{"hasChildren":false},"tocID":"con_preview_t-d4800e3339","topics":[]},{"title":"How do I view contacts as business cards?","shortdesc":"<p class=\"shortdesc\">From <span class=\"ph uicontrol\">My Contacts<\/span> or <span class=\"ph uicontrol\">Recent Contacts<\/span>, you can view your contacts as business cards, rather than as a list. You can also hover over a name in many places in Notes to view a business card for that person.<\/p>","href":"con_view_business_card_t.html","attributes":{"data-id":"con_view_businesscard_t.dita"},"menu":{"hasChildren":false},"tocID":"con_view_businesscard_t.dita-d4800e3350","topics":[]},{"title":"How do I change who is added to Recent Contacts?","shortdesc":"<p class=\"shortdesc\">The addresses that appear in Recent Contacts are chosen automatically, based on the people with whom you recently emailed, chatted, and had meetings. You can choose to stop adding names to Recent Contacts, or to add only some names, such as names from emails you send.<\/p>","href":"con_chg_recent_con_settings_t.html","attributes":{"data-id":"con_chg_recent_con_settings_t"},"menu":{"hasChildren":false},"tocID":"con_chg_recent_con_settings_t-d4800e3367","topics":[]}]}};