oxy_webhelp_navlinks = {"rep_app_options_basic_t-d4800e6192":{"topics":[{"title":"Setting special replication limitations","shortdesc":"<p class=\"shortdesc\">You can use the Other page of the Replication Settings dialog box to disable replication, replicate documents based on their age, set the replica's priority in the schedule, or set a CD-ROM publishing date for the selected replica. Options on the Other page apply to the selected replica in all locations.<\/p>","href":"rep_app_options_other_t.html","attributes":{"data-id":"rep_app_options_other_t"},"menu":{"hasChildren":false},"tocID":"rep_app_options_other_t-d4800e6208","topics":[]},{"title":"Setting replication defaults for all Notes applications","shortdesc":"<p class=\"shortdesc\">You can set defaults that apply to all local replicas of HCL NotesÂ® applications you create. These settings include whether the applications receive partial documents or attachments, when to create new local replicas, whether to change the encryption level or turn off encryption, and whether to create full-text indexes.<\/p>","href":"pref_rep_defaults_all_apps_r_2.html","attributes":{"data-id":"pref_rep_defaults_all_apps_r"},"menu":{"hasChildren":false},"tocID":"pref_rep_defaults_all_apps_r-d4800e6221","topics":[]}]}};