oxy_webhelp_navlinks = {"applications_toc-d4800e3606":{"topics":[{"title":"Getting started with Notes applications","shortdesc":"<p class=\"shortdesc\">An HCL NotesÂ® application contains information about a particular area of interest, such as the forms and policy documents for a department, or it might contain documents of a similar type, such as email messages. In addition, some companies create discussion applications, where employees can post responses to particular topics.<\/p>","href":"app_notes_applications_c.html","attributes":{"data-id":"app_notes_applications_c"},"menu":{"hasChildren":true},"tocID":"app_notes_applications_c-d4800e3617","next":"app_notes_applications_c-d4800e3617"},{"title":"Notes applications quick reference","shortdesc":"<p class=\"shortdesc\">This quick reference card is a list of instructions for common tasks and shortcuts for Notes applications, which you can print and keep at your desk for easy reference.<\/p>","href":"app_quickref_r.html","attributes":{"data-id":"ApplicationsQuickReference"},"menu":{"hasChildren":false},"tocID":"ApplicationsQuickReference-d4800e3700","topics":[]},{"title":"Advanced Notes applications topics","shortdesc":"<p class=\"shortdesc\">More advanced Notes applications tasks include determining which applications to back up, how to use XPages applications with Notes, and creating a subscription to an application.<\/p>","href":"app_adv.html","attributes":{"data-id":"app_adv.dita"},"menu":{"hasChildren":true},"tocID":"app_adv.dita-d4800e3711","next":"app_adv.dita-d4800e3711"}]}};