oxy_webhelp_navlinks = {"wid_advusers_c-d4800e9523":{"topics":[{"title":"Can I reuse a sidebar panel for the same widget action?","shortdesc":"<p class=\"shortdesc\">You can set a preference in a widget's XML to define whether a new sidebar panel is opened for each widget action or whether the same sidebar panel is reopened and overwritten for each action initiated by that widget.<\/p>","href":"wid_adv852_reusesidebarpanel_t.html","attributes":{"data-id":"wid_adv852_reusesidebarpanel_t"},"menu":{"hasChildren":false},"tocID":"wid_adv852_reusesidebarpanel_t-d4800e9548","topics":[]},{"title":"Can I hide a widget thumbnail?","shortdesc":"<p class=\"shortdesc\">You can control whether a widget's thumbnail is visible in the My Widgets sidebar panel.<\/p>","href":"wid_adv852_hidethumbnail_t.html","attributes":{"data-id":"wid_adv852_hidethumbnail_t"},"menu":{"hasChildren":false},"tocID":"wid_adv852_hidethumbnail_t-d4800e9573","topics":[]},{"title":"Can I set an overall default double-click action?","shortdesc":"<p class=\"shortdesc\">You can set a default double-click action to open widgets in the sidebar, a new window, a float window, or a tab. The default does not impact widgets that have a predetermined double-click action defined in their XML. The supplied default is New Window.<\/p>","href":"wid_adv852_defdblclick_t.html","attributes":{"data-id":"wid_adv852_defdblclick_t"},"menu":{"hasChildren":false},"tocID":"wid_adv852_defdblclick_t-d4800e9598","topics":[]},{"title":"Can I specify a custom widget icon?","shortdesc":"<p class=\"shortdesc\">Power users and administrators can add an attribute to the <code class=\"ph codeph\">palleteItem<\/code> element in a widget's extension.xml definition to specify a custom icon to use in the sidebar view\/title bar.<\/p>","href":"wid_adv852_custwidaction_t.html","attributes":{"data-id":"wid_adv852_custwidaction_t"},"menu":{"hasChildren":false},"tocID":"wid_adv852_custwidaction_t-d4800e9623","topics":[]},{"title":"Can I install a widget from a .zip file?","shortdesc":"<p class=\"shortdesc\">Users can install a widget that is supplied to them as a .zip file. <\/p>","href":"wid_adv852_instfromzip_t.html","attributes":{"data-id":"wid_adv852_instfromzip_t"},"menu":{"hasChildren":false},"tocID":"wid_adv852_instfromzip_t-d4800e9651","topics":[]}]}};